export interface Powerup {
  x: number;
  y: number;
  size: number;
  type: 'shield' | 'slowmo';
}

export const activePowerups: Powerup[] = [];

export const shieldImage = new Image();
shieldImage.src =
  'data:image/svg+xml;base64,';

const slowmoImage = new Image();
slowmoImage.src =
  'data:image/svg+xml;base64,';

export function spawnPowerup(canvas: HTMLCanvasElement): void {
  const powerupTypes: Powerup['type'][] = ['shield', 'slowmo'];
  const type = powerupTypes[Math.floor(Math.random() * powerupTypes.length)]!;

  const powerup: Powerup = {
    x: canvas.width,
    y: Math.random() * (canvas.height - 100) + 50, // Avoid spawning too close to edges
    size: 40,
    type: type,
  };
  activePowerups.push(powerup);
}

export function updatePowerups(gameSpeed: number): void {
  for (let i = activePowerups.length - 1; i >= 0; i--) {
    const powerup = activePowerups[i];
    if (!powerup) continue;

    powerup.x -= gameSpeed;

    // Remove power-up if it goes off-screen
    if (powerup.x + powerup.size < 0) {
      activePowerups.splice(i, 1);
    }
  }
}

export function drawPowerups(ctx: CanvasRenderingContext2D): void {
  activePowerups.forEach(powerup => {
    if (powerup.type === 'shield') {
      ctx.drawImage(
        shieldImage,
        powerup.x,
        powerup.y,
        powerup.size,
        powerup.size,
      );
    } else if (powerup.type === 'slowmo') {
      ctx.drawImage(
        slowmoImage,
        powerup.x,
        powerup.y,
        powerup.size,
        powerup.size,
      );
    }
  });
}
